import React, {Component} from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import './tabs.css'
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import Avatar from '@material-ui/core/Avatar';
import BeachAccessIcon from '@material-ui/icons/BeachAccess';
import Divider from '@material-ui/core/Divider';
import Typography from '@material-ui/core/Typography';
import CardWithAction from '../card/card-with-action';
const count = 0;
class TabComponent extends Component {
  constructor(props){
    super(props)
    this.state = {
      assetList : [],
      searchFeild : '',
      loading : true
    }
  }
  componentDidMount(){
    console.log('COMP MOUNTING>>>>:', this.props)
    this.setState({loading : false})
    console.log('MOUNT:',this.state)
    fetch('/assets')
    .then(res=> res.json() )
    .then(data => this.setState({ assetList: data }) )

  }


  render(){
    const { searchFeild, loading } = this.state;
    const assetList = this.props.data
    console.log('STATE ****LOADING FROM**** CLASS:',this)
    // console.log('LOADING:', this)

    return (
      <div>
        <Tabs>
          <TabList className='tabItem-container'>
            {console.log("CHECKING**********",this.state)}
            { !loading &&
              assetList.map( i=> 
                <Tab key={i.key}>
                  <List className='tabItem'>
                    <ListItem>
                      <ListItemAvatar>
                        <Avatar> 
                          {i.status == '1'? 'a' : 'i'}
                        </Avatar>
                      </ListItemAvatar>
                      <ListItemText primary={i.assetNumber} secondary={i.type} />
                    </ListItem>
                  <Divider component="li" />
                  </List>
                </Tab>  
            )} 

          </TabList>
            { !loading &&
              assetList.map( i=> 
                <TabPanel key={i.key} className='tab-panel'>
                  <CardWithAction  assetData={{
                                                      'asset': i.assetNumber,
                                                      'id': i.key,
                                                      'status': i.status,
                                                      'gpsid': i.gpsid}
                  }/>
                </TabPanel> )
            }
        </Tabs>
      </div>
    )

  }
  // // assetList.map(i=>console.log(i))

  // function checkLoading(arg){
  //   if (arg[0] != undefined){
  //     console.log('******************************')
  //     return setLoading(false)

  //   }
  //   console.log('+++++++++++++++++++++++++++++++')
  //   setLoading(true)
  //   return checkLoading(arg)
  // }
  // // checkLoading(assetList)

  // console.log('TESTING:',assetList[0], assetList.length)



}

export default TabComponent;
