import React, {Component} from 'react';

import {Tabs, TabList, Tab, PanelList, Panel} from 'react-tabtab';


export default class Basic extends Component {
    constructor(props) {
    super(props);
    this.handleTabChange = this.handleTabChange.bind(this);

    this.state = {
      activeIndex: 0,
      tabs: this.props.assets
    }
  }

    handleTabChange(index) {
    this.setState({activeIndex: index});
  }

    componentDidMount() {
      // this.props.assets.map((k, v) => console.log(k,v))
    // console.log(this.props)

    //   this.state.tabs.map((tab, index) => {
    //   this.state.tabsTemplate.push(<Tab key={index}>{tab.title}</Tab>)
    //   this.state.panelTemplate.push(<Panel key={index}>{tab.content}</Panel>)
    // })
    // console.log('THE ARRAY',tabsTemplate)
    }

  render() {
      const tabsTemplate = []
      const panelTemplate = []
      this.state.tabs.map((tab, index) => {
        this.state.tabsTemplate.push(<Tab key={index}>{tab.title}</Tab>)
        this.state.panelTemplate.push(<Panel key={index}>{tab.content}</Panel>)
    })


    return (
      <Tabs customStyle={this.props.customStyle}>
        <TabList>
          {tabsTemplate}
        </TabList>
        <PanelList>
          {panelTemplate}
        </PanelList>
      </Tabs>
    )
  }
}
