	// const { assetInfo : { id=null, gps=null }} = this.state
	const { data : { id=null, gpsid=null }} = this.state


	if (gpsid === null){
		console.log('This asset has no GPSID')
	}else{
		let bodyData = {
		'params': {
			'id': id,
